/*!

=========================================================
* Argon Dashboard React - v1.2.2
=========================================================

* Product Page: https://www.creative-tim.com/product/argon-dashboard-react
* Copyright 2022 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/argon-dashboard-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
// reactstrap components
import Chart from "chart.js";
// react plugin used to create charts
import { React, useRef, useState } from "react";
import { Link } from "react-router-dom";

import {
  Badge,
  Card,
  CardHeader,
  Col,
  DropdownMenu,
  DropdownItem,
  UncontrolledDropdown,
  DropdownToggle,
  NavItem,
  NavLink,
  Nav,
  Progress,
  Container,
  Row,
  CardBody,
  Button,
} from "reactstrap";

import { chartOptions, parseOptions } from "variables/charts.js";
// core components
import Header from "components/Headers/Header.js";
import axios from "axios";

const instance = axios.create({
  // 서버의 도메인 주소를 baseURL로 설정합니다.
  baseURL: "https://10.0.5.123:3000",
  withCredentials: true, // HTTP Only 쿠키를 사용하기 위해 설정합니다.
});

// CSRF 토큰을 서버로부터 받아와 HTTP Only 쿠키에 저장합니다.
instance.get("/api/csrf-token").then((response) => {
  const csrfToken = response.data.csrfToken;
  instance.defaults.headers.common["X-CSRF-Token"] = csrfToken;
});

// API 호출시 요청 헤더에 CSRF 토큰을 포함시킵니다.
instance.post("/admin/ssh", { data: "example" });
const SSh = () => {
  const [server, setServer] = useState("Select Server");
  const [showSSH, setShowSSH] = useState(false);
  const [value, setValue] = useState("CSD 0");
  const [buttonColors, setButtonColors] = useState([
    "success",
    "success",
    "success",
    "success",
    "success",
    "success",
    "success",
    "success",
  ]);
  const toggleButtonColor = (buttonName) => {
    const index = parseInt(buttonName.split(" ")[1]) - 1; // 버튼 이름에서 index 추출
    const newButtonColors = [...buttonColors];
    newButtonColors[index] =
      buttonColors[index] === "success" ? "danger" : "success";
    setButtonColors(newButtonColors);
  };

  const connctSSH = (e) => {
    setShowSSH(true);
    e.currentTarget.disabled = true;
  };
  const handleButtonClick = (e) => {
    let { value: buttonName } = e.target;
    if (buttonName === undefined) {
      buttonName = e.target.name;
    }
    setValue(buttonName);
    toggleButtonColor(buttonName);
  };
  if (window.Chart) {
    parseOptions(Chart, chartOptions());
  }
  const handleDropdownClick = (e) => {
    setServer(e.target.getAttribute("value"));
  };
  return (
    <>
      <Header />
      {/* Page content */}
      <Container className="mt--7" fluid>
        <Row>
          <Col>
            <Card className="shadow">
              <CardHeader className="border-0">
                <Row className="align-items-center">
                  <h1 className="col mb-0 text-primary">CSD Information</h1>
                </Row>
