export const checkPasswordStrength = (password) => {
  if (password.length <= 5) {
    if (password.length <= 0) {
      return { text: "", color: "" };
    }
    return { text: "Weak", color: "danger" };
  } else if (password.length <= 8) {
    return { text: "Normal", color: "warning" };
  } else {
    return { text: "Strong", color: "success" };
  }
};

export const handleSubmit = async (event) => {
  event.preventDefault();
  if (password !== passwordCheck) {
    setPasswordError(true);
  } else {
    setPasswordError(false);
  }

  try {
    const response = await axios.post("http://10.0.5.123:3000/auth/register", {
      username: user,
      password: password,
      passwordCheck: passwordCheck,
    });
    console.log(response.data);
  } catch (error) {
    console.error(error);
  }
};
export const handlePasswordChange = (e) => {
  setPassword(e.target.value);
  checkPasswordStrength(e.target.value);
};

export default { checkPasswordStrength, handleSubmit, handlePasswordChange };
