/*!

=========================================================
* Argon Dashboard React - v1.2.2
=========================================================

* Product Page: https://www.creative-tim.com/product/argon-dashboard-react
* Copyright 2022 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/argon-dashboard-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
// reactstrap components
import Chart from "chart.js";
// react plugin used to create charts
import { React, useRef, useState } from "react";
import { Link } from "react-router-dom";

import {
  Badge,
  Card,
  CardHeader,
  Col,
  DropdownMenu,
  DropdownItem,
  UncontrolledDropdown,
  DropdownToggle,
  NavItem,
  NavLink,
  Nav,
  Progress,
  Container,
  Row,
  CardBody,
  Button,
} from "reactstrap";

import { chartOptions, parseOptions } from "variables/charts.js";
// core components
import Header from "components/Headers/Header.js";

const SSh = () => {
  const { exec } = require("child_process");

  exec("ls", (error, stdout, stderr) => {
    if (error) {
      console.error(`exec error: ${error}`);
      return;
    }

    console.log(`stdout: ${stdout}`);
    console.error(`stderr: ${stderr}`);
  });
  const [value, setValue] = useState("CSD 0");
  const [buttonColor1, setButtonColor1] = useState("success");
  const [buttonColor2, setButtonColor2] = useState("success");
  const [buttonColor3, setButtonColor3] = useState("success");
  const [buttonColor4, setButtonColor4] = useState("success");
  const [buttonColor5, setButtonColor5] = useState("success");
  const [buttonColor6, setButtonColor6] = useState("success");
  const [buttonColor7, setButtonColor7] = useState("success");
  const [buttonColor8, setButtonColor8] = useState("success");

  const setButtonColor = (buttonName) => {
    switch (buttonName) {
      case "CSD 1":
        return buttonColor1;
      case "CSD 2":
        return buttonColor2;
      case "CSD 3":
        return buttonColor3;
      case "CSD 4":
        return buttonColor4;
      case "CSD 5":
        return buttonColor5;
      case "CSD 6":
        return buttonColor6;
      case "CSD 7":
        return buttonColor7;
      case "CSD 8":
        return buttonColor8;
      default:
        return "success";
    }
  };

  const toggleButtonColor = (buttonName) => {
    switch (buttonName) {
      case "CSD 1":
        setButtonColor1(buttonColor1 === "success" ? "danger" : "success");
        break;
      case "CSD 2":
        setButtonColor2(buttonColor2 === "success" ? "danger" : "success");
        break;
      case "CSD 3":
        setButtonColor3(buttonColor3 === "success" ? "danger" : "success");
        break;
      case "CSD 4":
        setButtonColor4(buttonColor4 === "success" ? "danger" : "success");
        break;
      case "CSD 5":
        setButtonColor5(buttonColor5 === "success" ? "danger" : "success");
        break;
      case "CSD 6":
        setButtonColor6(buttonColor6 === "success" ? "danger" : "success");
        break;
      case "CSD 7":
        setButtonColor7(buttonColor7 === "success" ? "danger" : "success");
        break;
      case "CSD 8":
        setButtonColor8(buttonColor8 === "success" ? "danger" : "success");
        break;
      default:
        break;
    }
  };

  const handleButtonClick = (e) => {
    const { value: buttonName } = e.target;
    setValue(buttonName);
    toggleButtonColor(buttonName);
  };
  if (window.Chart) {
    parseOptions(Chart, chartOptions());
  }

  return (
    <>
      <Header />
      {/* Page content */}
      <Container className="mt--7" fluid>
        <Row>
          <Col>
            <Card className="shadow">
              <CardHeader className="border-0">
                <Row className="align-items-center">
                  <h1 className="col mb-0 text-primary">CSD Information</h1>
                </Row>
                <hr className="mt-3 mb-0" />
              </CardHeader>
              <CardBody className="mt-0">
                <Row>
                  <div className="col-2 align-items-center text-right">
                    <UncontrolledDropdown nav>
                      <DropdownToggle className="pr-0" nav>
                        Select Server &nbsp;
                        <i className="ni ni-bold-down" />
                      </DropdownToggle>
                      <DropdownMenu className="dropdown-menu-arrow" right>
                        <DropdownItem>
                          <i className="ni ni-bold-right" />
                          <span>Server 10.0.0.100</span>
                        </DropdownItem>
                        <DropdownItem>
                          <i className="ni ni-bold-right" />
                          <span>Server 10.0.0.101</span>
                        </DropdownItem>
                        <DropdownItem>
                          <i className="ni ni-bold-right" />
                          <span>Server 10.0.0.102</span>
                        </DropdownItem>
                        <DropdownItem>
                          <i className="ni ni-bold-right" />
                          <span>Server 10.0.0.103</span>
                        </DropdownItem>
                        <DropdownItem>
                          <i className="ni ni-bold-right" />
                          <span>Server 10.0.0.104</span>
                        </DropdownItem>
                        <DropdownItem>
                          <i className="ni ni-bold-right" />
                          <span>Server 10.0.0.105</span>
                        </DropdownItem>
                        <DropdownItem>
                          <i className="ni ni-bold-right" />
                          <span>Server 10.0.0.106</span>
                        </DropdownItem>
                        <DropdownItem>
                          <i className="ni ni-bold-right" />
                          <span>Server 10.0.0.107</span>
                        </DropdownItem>
                      </DropdownMenu>
                    </UncontrolledDropdown>
                  </div>
                  <div className="col-10 align-items-center text-center">
                    <Button
                      color={setButtonColor("CSD 1")}
                      outline
                      className="mx-4"
                      value={"CSD 1"}
                      onClick={handleButtonClick}
                    >
                      <img
                        alt="placeholder"
                        src={require("../../assets/img/icons/icons8-ssd-64.png")}
                      />
                      <br />
                      CSD1
                    </Button>
                    <Button
                      color={setButtonColor("CSD 2")}
                      outline
                      className="mx-4"
                      value={"CSD 2"}
                      onClick={handleButtonClick}
                    >
                      <img
                        alt="placeholder"
                        src={require("../../assets/img/icons/icons8-ssd-64.png")}
                      />
                      <br />
                      CSD2
                    </Button>
                    <Button
                      color={setButtonColor("CSD 3")}
                      outline
                      className="mx-4"
                      value={"CSD 3"}
                      onClick={handleButtonClick}
                    >
                      <img
                        alt="placeholder"
                        src={require("../../assets/img/icons/icons8-ssd-64.png")}
                      />
                      <br />
                      CSD3
                    </Button>
                    <Button
                      color={setButtonColor("CSD 4")}
                      outline
                      className="mx-4"
                      value={"CSD 4"}
                      onClick={handleButtonClick}
                    >
                      <img
                        alt="placeholder"
                        src={require("../../assets/img/icons/icons8-ssd-64.png")}
                      />
                      <br />
                      CSD4
                    </Button>
                    <Button
                      color={setButtonColor("CSD 5")}
                      outline
                      className="mx-4"
                      value={"CSD 5"}
                      onClick={handleButtonClick}
                    >
                      <img
                        alt="placeholder"
                        src={require("../../assets/img/icons/icons8-ssd-64.png")}
                      />
                      <br />
                      CSD5
                    </Button>
                    <Button
                      color={setButtonColor("CSD 6")}
                      outline
                      className="mx-4"
                      value={"CSD 6"}
                      onClick={handleButtonClick}
                    >
                      <img
                        alt="placeholder"
                        src={require("../../assets/img/icons/icons8-ssd-64.png")}
                      />
                      <br />
                      CSD6
                    </Button>
                    <Button
                      color={setButtonColor("CSD 7")}
                      outline
                      className="mx-4"
                      value={"CSD 7"}
                      onClick={handleButtonClick}
                    >
                      <img
                        alt="placeholder"
                        src={require("../../assets/img/icons/icons8-ssd-64.png")}
                      />
                      <br />
                      CSD7
                    </Button>
                    <Button
                      color={setButtonColor("CSD 8")}
                      outline
                      className="mx-4"
                      value={"CSD 8"}
                      onClick={handleButtonClick}
                    >
                      <img
                        alt="placeholder"
                        src={require("../../assets/img/icons/icons8-ssd-64.png")}
                      />
                      <br />
                      CSD8
                    </Button>
                  </div>
                </Row>
              </CardBody>
            </Card>
          </Col>
        </Row>
        <Row className="mt-5">
          <Col xl="12">
            <Card className="shadow">
              <CardHeader className="border-0">
                <Row className="align-items-center">
                  <h1 className="col mb-0 text-primary">SSH Connection</h1>
                </Row>
                <hr className="mt-3 mb-0" />
              </CardHeader>
              <CardBody>
                <Row className="align-items-center">
                  <div className="row col-6">
                    <h2 className="mb-0 mx-2">Selected CSD</h2>
                    <Badge className="badge-lg text-sm" color="success">
                      {value}
                    </Badge>
                  </div>

                  <Nav className="mx-3" navbar>
                    <NavItem>
                      <NavLink
                        className="nav-link"
                        to="/admin/assistant"
                        tag={Link}
                      >
                        <Button
                          className="btn-icon btn-3"
                          color="info"
                          type="button"
                        >
                          <span className="btn-inner--icon">
                            <i className="ni ni-button-play" />
                          </span>
                          <span className="btn-inner--text">
                            CSD Info&Status
                          </span>
                        </Button>
                      </NavLink>
                    </NavItem>
                  </Nav>

                  <Button
                    className="btn-icon btn-3"
                    color="primary"
                    disabled
                    type="button"
                  >
                    <span className="btn-inner--icon">
                      <i className="ni ni-button-play" />
                    </span>
                    <span className="btn-inner--text">Connect SSH</span>
                  </Button>
                  <Button
                    className="btn-icon btn-3"
                    color="success"
                    type="button"
                  >
                    <span className="btn-inner--icon">
                      <i className="ni ni-button-play" />
                    </span>
                    <span className="btn-inner--text">Show Snippet</span>
                  </Button>
                </Row>
                <Row className="align-items-center text-center mt-2">
                  <div className="row col-4 align-items-center justify-content-center">
                    <span className="mr-2">Full Recover</span>
                    <div>
                      <label className="custom-toggle mt-3">
                        <input type="checkbox" />
                        <span className="custom-toggle-slider rounded-circle " />
                      </label>
                    </div>
                  </div>
                  <div className="col-5">
                    <div className="progress-wrapper">
                      <div className="progress-info">
                        <div className="progress-label">
                          <span>Recover</span>
                        </div>
                        <div className="progress-percentage">
                          <span>78%</span>
                        </div>
                      </div>
                      <Progress max="100" value="78" />
                    </div>
                  </div>
                  <div className="col-2">
                    <Button color="primary">Start Recover</Button>
                  </div>
                </Row>
                <Row className="align-items-center text-center mt-2">
                  <img
                    className="col-12"
                    alt="placeholder"
                    src={require("../../assets/img/tpch/testlog.png")}
                  />
                </Row>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default SSh;
